import React, { useEffect, useState } from 'react';
import * as Api from '../ApiHandle';

const Message = ()=>{
    const [allData, setAllData] = useState([]);
    useEffect(()=>{
        const messages = async ()=>{
            let apiLink = '/messages';
            let resp = await Api.apiHandle(apiLink,'','get');
            setAllData(()=>{
                return resp.data;
            }) 
        }
        messages();
    },[]);    
    const displayMsg = allData.map((v)=>(
    <h1 key={v._id}>msg: {v.text}, username: {v.user._id}, date: {v.createdAt}</h1>
    ))
    return(
        <>
            <h1>Messages</h1>
            <div className="container">
            <div className="row">
            <li style={{display: 'flex'}} className="list-group-item col-md-8"><img src="https://warbler-client-jbh.herokuapp.com/static/media/warbler-logo.25398cf7.png" alt="sdfasfsd" height="100" width="100" className="timeline-image" /><div className="message-area"><a href="/">@sdfasfsd &nbsp;</a><span className="text-muted"><time datetime="1596440009706" className="text-muted">03 Aug 2020</time></span><p>hi</p></div></li>
            </div>
            </div>
            {displayMsg}
        </>
    )
};

export default Message;
