import axios from 'axios';

export const apiHandle = (apiLink,apiData,method)=>{
    const baseUrl = 'http://localhost:8081/api';
    return axios({
        method: method,
        url: `${baseUrl}${apiLink}`,
        data: apiData
    })
    .then(response => {
        console.log(response);
        return response;
    })
    .catch(error => {
        return error.response;
    });
}
