import React, { useEffect } from 'react';
import * as Api from '../ApiHandle';

const Message = ()=>{
    const allData = [];
    useEffect(()=>{
        const messages = async ()=>{
            let apiLink = '/messages';
            let resp = await Api.apiHandle(apiLink,'','get');
            allData.push(resp.data); 
        }
        messages();
    },[]);    
    console.log(allData);
    return(
        <>
            <h1>Messages</h1>
        </>
    )
};

export default Message;
