import React, {useState} from 'react';
import AuthIndex from './Index';

const Logout = ()=>{
    const [redirect,setRedirect] = useState(false);
    const logout = ()=>{
        localStorage.removeItem('isLogin');
        setRedirect(true);
    }
    logout();
    return(
        <>
            {redirect === true? <AuthIndex/>:null }
            <h1>Logout</h1>
        </>
    )
};

export default Logout;
