import React, { useEffect, useState } from 'react';
import * as Api from '../ApiHandle';

const Message = ()=>{
    const [allData, setAllData] = useState([]);
    useEffect(()=>{
        const messages = async ()=>{
            let apiLink = '/messages';
            let resp = await Api.apiHandle(apiLink,'','get');
            setAllData(()=>{
                return resp.data;
            }) 
        }
        messages();
    },[]);    
    const displayMsg = allData.map((v)=>(
    <h1 key={v._id}>msg: {v.text}, username: {v.user._id}, date: {v.createdAt}</h1>
    ))
    return(
        <>
            <h1>Messages</h1>
            <li class="list-group-item">ss<img src="" alt="sdfasfsd" height="100" width="100" class="timeline-image" /><div class="message-area"><a href="/">@sdfasfsd &nbsp;</a><span class="text-muted"><time datetime="1596440009706" class="text-muted">03 Aug 2020</time></span><p>hi</p></div></li>
            {displayMsg}
        </>
    )
};

export default Message;
