import * as Api from '../ApiHandle';

const Message = ()=>{
    useEffect(()=>{
        const messages = async ()=>{
            let apiLink = '/auth/messages';
            let resp = await Api.apiHandle(apiLink,'','get'); 
        }
        message();
    },[]);    
    return(
        <>
            <h1>Messages</h1>
        </>
    )
};

export default Message;
