import React, { useState } from 'react';
import { Switch, Route } from 'react-router-dom';
import axios from 'axios';
import Signin from './Signin';
import Signup from './Signup';


const Index = ()=>{
    const [loginData, setLoginData] = useState({
        email: '',
        password: '',
        username: ''
    });
    const [isLogin, setIsLogin] = useState(false);

    const handleAuthChange = (e)=>{
        const {name, value} = e.target;
        setLoginData((prevData)=>{
            return {
                ...prevData,
                [name]: value
            }
        });
    }

    const apiHandle = (apiLink,apiData,method)=>{
        const baseUrl = 'http://localhost:8081/api/';
        axios({
            method: method,
            url: `${baseUrl}${apiLink}`,
            data: apiData
        })
        .then(response => {
            return response;
        })
        .catch(error => {
            return error.response;
        });
    }

    const handleAuthSubmit = async (e)=>{
        e.preventDefault();
        let apiLink = '/auth/signin';
        let apiData = {
            email: loginData.email,
            password: loginData.password
        }
        await apiHandle(apiLink,apiData,'post'); 
        setIsLogin(true);
    }

    const handleSignUpSubmit = async (e)=>{
        e.preventDefault();
        let apiLink = '/auth/signup';
        let apiData = loginData
        await apiHandle(apiLink,apiData,'post'); 
        setIsLogin(true);
    }
    return(
        <>
            <Switch>
                <Route exact path="/login" >
                    <Signin 
                    handleChange={handleAuthChange}
                    handleAuthSubmit={handleAuthSubmit}
                    email={loginData.email}
                    password={loginData.password}
                    />
                </Route>
                <Route exact path="/register" >
                    <Signup 
                    handleChange={handleAuthChange}
                    handleAuthSubmit={handleSignUpSubmit}
                    email={loginData.email}
                    password={loginData.password}
                    username={loginData.username}
                    />
                </Route>
            </Switch>
         </>
    )
};

export default Index;
